						case 2: // Elvine can't invi on SW side 
							if (m_pClientList[iClientH]->m_sX + 20 < m_pClientList[iClientH]->m_sY) goto MAGIC_NOEFFECT; 	
							break;
						case 1: // Ares can't invi on NE side
