						case 2: 
						case 6:
						case 8: 
						case 9:
						case 12:
						case 13:  // Ogres 
							m_pMapList[iMapIndex]->m_stSpotMobGenerator[iSMGRCfgIndex].iMobType = 29;  
							iAdjustMobNum = 0;
								strcpy(cNpcName,"Ogre"); 
