int CGame::iGetExpLevel(int iExp)
{ register int i;
	for (i = 1; i < 1001; i++) 
	if ((m_iLevelExpTable[i] <= iExp) && (m_iLevelExpTable[i+1] > iExp)) return i;
	return 0;
}

