		case 633: // demon
		case 634: // wizard
		case 635: // mage
		case 636: // grand mage
		case 637: // light pro neck
		case 638: // fire pro neck
		case 639: // Poison pro
		case 640: // sufferent
		case 641: // medusa
		case 642: // ice pro
		case 643: // ice elem
		case 644: // air elem
		case 645: // effreti
		case 646:  // Beholder
